class Solution:
    def numberOfRightTriangles(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        row_counts = [sum(row) for row in grid]
        col_counts = [sum(grid[i][j] for i in range(rows)) for j in range(cols)]

        triangles = 0
        for i in range(rows):
            for j in range(cols):
                if grid[i][j] == 1:
                    triangles += (row_counts[i] - 1) * (col_counts[j] - 1)

        return triangles
