class Solution:
    def minAnagramLength(self, s: str) -> int:
        from collections import Counter
        n = len(s)
        counter = Counter(s)
        gcd = -1
        for v in counter.values():
            if gcd == -1:
                gcd = v
            else:
                gcd = self.gcd(gcd, v)
        return gcd

    def gcd(self, a, b):
        while b:
            a, b = b, a % b
        return a
```

