class Solution:
    def isValid(self, word: str) -> bool:
        vowels = set('aeiouAEIOU')
        consonants = set('bcdfghjklmnpqrstvwxyzBCDFGHJKLMNPQRSTVWXYZ')

        if len(word) < 3:
            return False

        if not set(word).issubset(set('0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ')):
            return False

        if not set(word) & vowels:
            return False

        if not set(word) & consonants:
            return False

        return True
