class Solution:
    def findProductsOfElements(self, queries: List[List[int]]) -> List[int]:
        MOD = 10**5
        N = 10**6
        pw2 = [1]
        for _ in range(N):
            pw2.append(pw2[-1] * 2 % MOD)
        pw2_inv = [pow(pw2[i], MOD - 2, MOD) for i in range(N + 1)]

        def power_array(n):
            res = 1
            pw = 1
            while n:
                if n & 1:
                    res = res * pw % MOD
                pw = pw * pw % MOD
                n >>= 1
            return res

        res = []
        for frm, to, mod in queries:
            pw = 1
            for i in range(frm, to + 1):
                pw = pw * power_array(i) % MOD
            res.append(pw % mod)
        return res
```

