        MOD = 10**5
        N = 10**6
        pow2 = [1]
        for _ in range(N):
            pow2.append((pow2[-1] * 2) % MOD)

        pref = [1]
        for _ in range(N):
            pref.append((pref[-1] * pow2[_]) % MOD)

        res = []
        for from_i, to_i, mod_i in queries:
            res.append((pref[to_i + 1] * pow(pref[from_i], MOD - 2, MOD)) % mod_i)

        return res
