class Solution:
    def findProductsOfElements(self, queries: List[List[int]]) -> List[int]:
        MOD = 10**5
        N = 10**6
        pw2 = [1]
        for _ in range(N):
            pw2.append(pw2[-1] * 2 % MOD)

        def powerful_array(n):
            res = []
            i = 0
            while n > 0:
                if (n & 1):
                    res.append(pw2[i])
                n >>= 1
                i += 1
            return res

        big_nums = []
        for i in range(1, N + 1):
            big_nums.extend(powerful_array(i))

        res = []
        for q in queries:
            from_i, to_i, mod_i = q
            prod = 1
            for i in range(from_i, to_i + 1):
                for num in powerful_array(i):
                    prod = (prod * num) % mod_i
            res.append(prod)
        return res
