from typing import List
from collections import deque

class Solution:
    def findWinningPlayer(self, skills: List[int], k: int) -> int:
        n = len(skills)
        idx = list(range(n))
        idx.sort(key=lambda x: skills[x], reverse=True)
        winner_idx = idx[0]
        wins = 0
        queue = deque(range(n))

        while wins < k:
            p1 = queue.popleft()
            p2 = queue.popleft()
            if skills[p1] > skills[p2]:
                queue.appendleft(p1)
                queue.append(p2)
                if p1 == winner_idx:
                    wins += 1
                else:
                    wins = 0
            else:
                queue.appendleft(p2)
                queue.append(p1)
                if p2 == winner_idx:
                    wins += 1
                else:
                    wins = 0

        return winner_idx
```

