from typing import List
from collections import deque

class Solution:
    def findWinningPlayer(self, skills: List[int], k: int) -> int:
        n = len(skills)
        queue = deque(range(n))
        skill_dict = {skill: idx for idx, skill in enumerate(skills)}
        win_count = {idx: 0 for idx in range(n)}

        while True:
            player1 = queue.popleft()
            player2 = queue.popleft()
            if skills[player1] > skills[player2]:
                winner, loser = player1, player2
            else:
                winner, loser = player2, player1
            win_count[winner] += 1
            if win_count[winner] == k:
                return winner
            queue.appendleft(winner)
            queue.append(loser)
```

