class Solution:
    def waysToReachStair(self, k: int) -> int:
        MOD = 10**9 + 7
        dp = [0]*(k+1)
        dp[0] = 1
        for i in range(k+1):
            for j in range(i):
                if j + (1 << (i-j).bit_length()-1) <= i:
                    dp[i] += dp[j]
                    dp[i] %= MOD
        return dp[k] * 2 % MOD
```

