from collections import deque

class Solution:
    def minimumDiameterAfterMerge(self, edges1: List[List[int]], edges2: List[List[int]]) -> int:
        def bfs(graph, start):
            queue = deque([(start, 0)])
            visited = {start}
            max_depth = 0
            while queue:
                node, depth = queue.popleft()
                max_depth = max(max_depth, depth)
                for neighbor in graph[node]:
                    if neighbor not in visited:
                        visited.add(neighbor)
                        queue.append((neighbor, depth + 1))
            return max_depth

        graph1 = [[] for _ in range(len(edges1) + 1)]
        graph2 = [[] for _ in range(len(edges2) + 1)]

        for u, v in edges1:
            graph1[u].append(v)
            graph1[v].append(u)
        for u, v in edges2:
            graph2[u].append(v)
            graph2[v].append(u)

        max_depth1 = max(bfs(graph1, i) for i in range(len(edges1) + 1))
        max_depth2 = max(bfs(graph2, i) for i in range(len(edges2) + 1))

        return max(max_depth1, max_depth2) + 1
```

