class Solution:
    def validStrings(self, n: int) -> List[str]:
        def is_valid(s: str) -> bool:
            for i in range(len(s) - 1):
                if s[i:i+2] == "00":
                    return False
            return True

        def generate_strings(n: int) -> List[str]:
            if n == 1:
                return ["0", "1"]
            strings = []
            for s in generate_strings(n - 1):
                strings.append("0" + s)
                strings.append("1" + s)
            return [s for s in strings if is_valid(s)]

        return generate_strings(n)
