class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [1] * n
        for i in range(1, n):
            if nums[i] == nums[i - 1]:
                dp[i] = dp[i - 1] + 1
            else:
                dp[i] = 1
        res = max(dp)
        for i in range(n):
            cnt = 0
            for j in range(i, n):
                if nums[j] != nums[i]:
                    cnt += 1
                if cnt > k:
                    break
                res = max(res, j - i + 1)
        return res
```

