class Solution:
    def minimumArea(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        row_first, row_last = [float('inf')] * rows, [-float('inf')] * rows
        col_first, col_last = [float('inf')] * cols, [-float('inf')] * cols

        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 1:
                    row_first[r] = min(row_first[r], c)
                    row_last[r] = max(row_last[r], c)
                    col_first[c] = min(col_first[c], r)
                    col_last[c] = max(col_last[c], r)

        min_area = float('inf')
        for r in range(rows):
            if row_first[r] != float('inf'):
                for c in range(cols):
                    if col_first[c] != float('inf'):
                        area = (row_last[r] - row_first[r] + 1) * (col_last[c] - col_first[c] + 1)
                        min_area = min(min_area, area)

        return min_area
```

