            return False

        for x in range(xCorner + 1):
            for y in range(yCorner + 1):
                if not is_valid(x, y):
                    if x > 0 and is_valid(x - 1, y) and (y == 0 or is_valid(x, y - 1)):
                        return False
                    if y > 0 and is_valid(x, y - 1) and (x == 0 or is_valid(x - 1, y)):
                        return False

        return True
