class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        dp = {0: 0}
        for i in range(len(words)):
            for j in range(len(target) - 1, -1, -1):
                if target[j - len(words[i]) + 1:j + 1] == words[i]:
                    dp[j + 1] = min(dp.get(j + 1, float('inf')), dp.get(j - len(words[i]) + 1, float('inf')) + costs[i])
        return dp.get(len(target), -1)
```

