class Solution:
    def maxPossibleScore(self, start: List[int], d: int) -> int:
        start.sort()
        res = float('-inf')
        for i in range(len(start)):
            for j in range(i, len(start)):
                mid = (start[i] + start[j] + d) // 2
                score = min(mid - start[i], start[j] + d - mid)
                res = max(res, score)
        return res
