class Solution:
    def maximumLength(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [1] * n
        for i in range(1, n):
            for j in range(i):
                if (nums[i] + nums[j]) % 2 == (nums[j] + nums[j-1]) % 2 if j > 0 else True:
                    dp[i] = max(dp[i], dp[j] + 1)
        return max(dp)
```

