import heapq

class Solution:
    def resultsArray(self, queries: List[List[int]], k: int) -> List[int]:
        obstacles = []
        results = []

        for x, y in queries:
            distance = abs(x) + abs(y)
            heapq.heappush(obstacles, distance)

            if len(obstacles) < k:
                results.append(-1)
            else:
                results.append(heapq.nsmallest(k, obstacles)[-1])

        return results
```

