class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        left, right = {}, {}
        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - i - 1])
            left[diff] = left.get(diff, 0) + 1
            right[diff] = right.get(diff, 0) + 1
        res = n
        for x in range(k + 1):
            cnt = 0
            for i in range(n // 2):
                diff = abs(nums[i] - nums[n - i - 1] - x)
                if diff <= k:
                    cnt += min(left.get(diff, 0), right.get(x - diff, 0))
            res = min(res, n - cnt)
        return res
```

