class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = [{} for _ in range(n // 2)]
        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - i - 1])
            count[i][diff] = count[i].get(diff, 0) + 1
        res = n
        for i in range(k + 1):
            temp = 0
            for j in range(n // 2):
                temp += min(count[j].get((i + nums[j]) % (i * 2 + 1), 0), count[j].get((i + nums[n - j - 1]) % (i * 2 + 1), 0))
            res = min(res, n - temp * 2)
        return res
```

