class Solution:
    def maximumTotalSum(self, maximumHeight: List[int]) -> int:
        max_heights = list(set(maximumHeight))
        max_heights.sort(reverse=True)

        total_sum = 0
        used_heights = set()

        for height in max_heights:
            if height not in used_heights:
                total_sum += height
                used_heights.add(height)
            else:
                break

        if len(used_heights) == len(maximumHeight):
            return total_sum
        else:
            return -1
