class Solution:
    def canAliceWin(self, nums: List[int]) -> bool:
        total_sum = sum(nums)
        single_digits = sum(num for num in nums if num < 10)
        double_digits = sum(num for num in nums if num >= 10)

        for num in nums:
            if total_sum - num > single_digits and total_sum - num > double_digits:
                return True

        return False
