            graph[i].append(i + 1)

        for u, v in queries:
            graph[u].append(v)

        res = []
        for i in range(len(queries)):
            temp_graph = [[] for _ in range(n)]
            for j in range(n - 1):
                temp_graph[j].append(j + 1)
            for j in range(i + 1):
                u, v = queries[j]
                temp_graph[u].append(v)

            queue = deque([(0, 0)])
            visited = [False] * n
            visited[0] = True
            while queue:
                node, dist = queue.popleft()
                if node == n - 1:
                    res.append(dist)
                    break
                for neighbor in temp_graph[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        queue.append((neighbor, dist + 1))

        return res
