class Solution:
    def winningPlayerCount(self, n: int, pick: List[List[int]]) -> int:
        color_count = {}
        for player, color in pick:
            if color not in color_count:
                color_count[color] = {}
            if player not in color_count[color]:
                color_count[color][player] = 0
            color_count[color][player] += 1

        winners = 0
        for color in color_count.values():
            for player, count in color.items():
                if count > player:
                    winners += 1
                    break

        return winners
```

