class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        rows, cols = [0] * m, [0] * n

        for i in range(m):
            for j in range(n):
                rows[i] += grid[i][j] << (n - 1 - j)
                cols[j] += grid[i][j] << (m - 1 - i)

        res = float('inf')
        for i in range(1 << n):
            flip = sum(bin(i ^ rows[j]).count('1') for j in range(m))
            res = min(res, flip)

        for i in range(1 << m):
            flip = sum(bin(i ^ cols[j]).count('1') for j in range(n))
            res = min(res, flip)

        return res
```

