class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        res = []
        dp = [[0] * 2 for _ in range(n + 1)]

        for i in range(n):
            dp[i + 1][colors[i]] = dp[i][1 - colors[i]] + 1
            dp[i + 1][1 - colors[i]] = dp[i][colors[i]]

        for query in queries:
            if query[0] == 1:
                size = query[1]
                count = 0
                for i in range(n):
                    if dp[(i + size) % n][colors[i]] - dp[i][colors[i]] == size:
                        count += 1
                res.append(count)
            else:
                index, color = query[1], query[2]
                colors[index] = color
                dp = [[0] * 2 for _ in range(n + 1)]
                for i in range(n):
                    dp[i + 1][colors[i]] = dp[i][1 - colors[i]] + 1
                    dp[i + 1][1 - colors[i]] = dp[i][colors[i]]

        return res
```

