class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        dp = [[0] * 2 for _ in range(n + 1)]
        for i in range(n):
            dp[i + 1][colors[i]] = dp[i][1 - colors[i]] + 1
        res = []
        for query in queries:
            if query[0] == 1:
                size = query[1]
                count = 0
                for i in range(n):
                    if dp[(i + size - 1) % n + 1][colors[i]] - dp[i + 1][colors[i]] == size:
                        count += 1
                res.append(count)
            else:
                index, color = query[1], query[2]
                for i in range(2):
                    dp[index + 1][i] = dp[index][1 - i] + (i == color)
                    for j in range(index + 2, n + 1):
                        dp[j][i] = dp[j - 1][1 - i] + (i == colors[j - 1])
                    for j in range(index - 1, -1, -1):
                        dp[j][i] = dp[j + 1][1 - i] + (i == colors[j])
        return res
```

