class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        dp = [[0] * (n + 1) for _ in range(2)]
        for i in range(n):
            dp[colors[i]][i + 1] = dp[1 - colors[i]][i] + 1
        res = []
        for query in queries:
            if query[0] == 1:
                size = query[1]
                count = 0
                for i in range(n):
                    if dp[colors[i]][(i + size - 1) % n + 1] - dp[colors[i]][i + 1] == size - 1:
                        count += 1
                res.append(count)
            else:
                index, color = query[1], query[2]
                dp[color][index + 1] = dp[1 - color][index] + 1
                dp[1 - color][index + 1] = dp[color][index]
                colors[index] = color
