class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        dp = [[0] * 2 for _ in range(n + 1)]
        for i in range(n):
            dp[i + 1][colors[i]] = dp[i][1 - colors[i]] + 1
        res = []
        for q in queries:
            if q[0] == 1:
                size = q[1]
                cnt = 0
                for i in range(n):
                    if dp[(i + size) % n][1 - colors[i]] - dp[i][colors[i]] > 0:
                        cnt += 1
                res.append(cnt)
            else:
                idx, color = q[1], q[2]
                dp[idx + 1][color] = dp[idx][1 - color] + 1
                for i in range(idx + 1, n + 1):
                    dp[i][1 - color] = dp[i - 1][color] + 1
                for i in range(idx - 1, -1, -1):
                    dp[i][1 - color] = dp[i + 1][color] + 1
                colors[idx] = color
        return res
