class Solution:
    def countPairs(self, nums: List[int]) -> int:
        def is_almost_equal(a, b):
            str_a, str_b = str(a), str(b)
            if len(str_a) != len(str_b):
                return False
            diff_count = 0
            diff_a, diff_b = [], []
            for i in range(len(str_a)):
                if str_a[i] != str_b[i]:
                    diff_count += 1
                    diff_a.append(str_a[i])
                    diff_b.append(str_b[i])
                if diff_count > 2:
                    return False
            if diff_count == 2 and set(diff_a) == set(diff_b):
                return True
            return False

        count = 0
        for i in range(len(nums)):
            for j in range(i + 1, len(nums)):
                if is_almost_equal(nums[i], nums[j]):
                    count += 1
        return count
```

