from collections import Counter
from itertools import permutations

class Solution:
    def countGoodIntegers(self, n: int, k: int) -> int:
        def is_k_palindromic(num: int) -> bool:
            num_str = str(num)
            if len(num_str) != n:
                return False
            if num % k != 0:
                return False
            if num_str != num_str[::-1]:
                return False
            return True

        count = 0
        for num in range(10**(n-1), 10**n):
            counter = Counter(str(num))
            for p in permutations(range(10), n):
                if all(counter[str(d)] >= p.count(d) for d in range(10)):
                    if is_k_palindromic(int(''.join(map(str, p)))):
                        count += 1
                        break
        return count
```

