from collections import Counter
from itertools import permutations

class Solution:
    def countGoodIntegers(self, n: int, k: int) -> int:
        def is_k_palindromic(x: int) -> bool:
            str_x = str(x)
            return str_x == str_x[::-1] and x % k == 0

        count = 0
        for p in permutations(range(10**(n-1), 10**n), n):
            counter = Counter(p)
            for x in range(10**(n-1), 10**n):
                if all(counter[d] >= c for d, c in Counter(str(x)).items()):
                    if is_k_palindromic(x):
                        count += 1
                        break
        return count
