from itertools import permutations
from math import factorial

class Solution:
    def countGoodIntegers(self, n: int, k: int) -> int:
        count = 0
        for digits in permutations(range(10), n):
            if digits[0] != 0:
                num_str = ''.join(map(str, digits))
                for p in permutations(num_str):
                    x = int(''.join(p))
                    if str(x) == str(x)[::-1] and x % k == 0:
                        count += 1
                        break
        return count // (factorial(n) // (factorial(n // 2) ** 2) if n % 2 == 0 else factorial(n) // (factorial((n - 1) // 2) ** 2))
