class TrieNode:
    def __init__(self):
        self.children = {}
        self.value = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, num):
        node = self.root
        for i in range(31, -1, -1):
            bit = (num >> i) & 1
            if bit not in node.children:
                node.children[bit] = TrieNode()
            node = node.children[bit]
        node.value = num

    def query(self, num):
        node = self.root
        for i in range(31, -1, -1):
            bit = (num >> i) & 1
            if 1 - bit in node.children:
                node = node.children[1 - bit]
            else:
                node = node.children[bit]
        return node.value

class Solution:
    def maximumSubarrayXor(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        trie = Trie()
        for num in nums:
            trie.insert(num)

        res = []
        for l, r in queries:
            max_xor = 0
            xor = 0
            node = trie.root
            for i in range(l, r + 1):
                xor ^= nums[i]
                node = trie.root
                for j in range(31, -1, -1):
                    bit = (xor >> j) & 1
                    if 1 - bit in node.children:
                        node = node.children[1 - bit]
                    else:
                        node = node.children[bit]
                max_xor = max(max_xor, xor ^ node.value)
            res.append(max_xor)

        return res
```

