class Solution:
    def findSafeWalk(self, grid: List[List[int]], health: int) -> bool:
        m, n = len(grid), len(grid[0])
        dp = [[float('-inf')] * n for _ in range(m)]
        dp[0][0] = health - grid[0][0]

        for i in range(m):
            for j in range(n):
                if i == 0 and j == 0:
                    continue
                for x, y in [(i-1, j), (i, j-1)]:
                    if 0 <= x < m and 0 <= y < n:
                        dp[i][j] = max(dp[i][j], dp[x][y] - grid[i][j])

        return dp[m-1][n-1] >= 0
```

