from collections import deque
from typing import List

class Solution:
    def maxMoves(self, kx: int, ky: int, positions: List[List[int]]) -> int:
        directions = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]

        def bfs(x, y, target_x, target_y):
            queue = deque([(x, y, 0)])
            visited = {(x, y)}
            while queue:
                x, y, step = queue.popleft()
                if (x, y) == (target_x, target_y):
                    return step
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < 50 and 0 <= ny < 50 and (nx, ny) not in visited:
                        queue.append((nx, ny, step + 1))
                        visited.add((nx, ny))
            return float('inf')

        memo = {}
        def dfs(positions):
            if not positions:
                return 0
            if tuple(map(tuple, positions)) in memo:
                return memo[tuple(map(tuple, positions))]
            max_move = 0
            for i, (x, y) in enumerate(positions):
                move = bfs(kx, ky, x, y)
                new_positions = positions[:i] + positions[i+1:]
                max_move = max(max_move, move + dfs(new_positions))
            memo[tuple(map(tuple, positions))] = max_move
            return max_move

        return dfs(positions)
```

