from collections import deque
from typing import List

class Solution:
    def maxMoves(self, kx: int, ky: int, positions: List[List[int]]) -> int:
        directions = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]
        memo = {}

        def bfs(x, y, target_x, target_y):
            if (x, y, target_x, target_y) in memo:
                return memo[(x, y, target_x, target_y)]
            queue = deque([(x, y, 0)])
            visited = {(x, y)}
            while queue:
                x, y, step = queue.popleft()
                if (x, y) == (target_x, target_y):
                    memo[(x, y, target_x, target_y)] = step
                    return step
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < 50 and 0 <= ny < 50 and (nx, ny) not in visited:
                        queue.append((nx, ny, step + 1))
                        visited.add((nx, ny))
            return float('inf')

        def dfs(i, x, y, total):
            if i == len(positions):
                return total
            max_total = 0
            for j in range(i, len(positions)):
                px, py = positions[j]
                step = bfs(x, y, px, py)
                if step == float('inf'):
                    continue
                new_total = dfs(i + 1, px, py, total + step)
                max_total = max(max_total, new_total)
            return max_total

        return dfs(0, kx, ky, 0)
