        directions = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]
        memo = {}

        def bfs(x, y, target_x, target_y):
            if (x, y, target_x, target_y) in memo:
                return memo[(x, y, target_x, target_y)]
            queue = deque([(x, y, 0)])
            visited = {(x, y)}
            while queue:
                x, y, step = queue.popleft()
                if (x, y) == (target_x, target_y):
                    memo[(x, y, target_x, target_y)] = step
                    return step
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < 50 and 0 <= ny < 50 and (nx, ny) not in visited:
                        queue.append((nx, ny, step + 1))
                        visited.add((nx, ny))
            memo[(x, y, target_x, target_y)] = float('inf')
            return float('inf')

        def dfs(i, x, y, pawns):
            if i == len(pawns):
                return 0
            res = float('-inf')
            for j in range(i, len(pawns)):
                pawn_x, pawn_y = pawns[j]
                step = bfs(x, y, pawn_x, pawn_y)
                if step == float('inf'):
                    continue
                new_pawns = pawns[:j] + pawns[j + 1:]
                new_x, new_y = pawn_x, pawn_y
                res = max(res, step + dfs(i + 1, new_x, new_y, new_pawns))
            return res

        return dfs(0, kx, ky, positions)
