class Solution:
    def maxMoves(self, kx: int, ky: int, positions: List[List[int]]) -> int:
        directions = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]

        def bfs(x: int, y: int, target_x: int, target_y: int) -> int:
            queue = deque([(x, y, 0)])
            visited = {(x, y)}
            while queue:
                x, y, step = queue.popleft()
                if x == target_x and y == target_y:
                    return step
                for dx, dy in directions:
                    nx, ny = x + dx, y + dy
                    if 0 <= nx < 50 and 0 <= ny < 50 and (nx, ny) not in visited:
                        queue.append((nx, ny, step + 1))
                        visited.add((nx, ny))
            return float('inf')

        memo = {}
        def dfs(i: int, x: int, y: int) -> int:
            if i == len(positions):
                return 0
            if (i, x, y) in memo:
                return memo[(i, x, y)]
            res = 0
            for j in range(i, len(positions)):
                px, py = positions[j]
                step = bfs(x, y, px, py)
                res = max(res, step + dfs(j + 1, px, py))
            memo[(i, x, y)] = res
            return res

        return dfs(0, kx, ky)
