class Solution:
    def maxScore(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        dp = [[set() for _ in range(n)] for _ in range(m)]

        for i in range(m):
            for j in range(n):
                dp[i][j].add(grid[i][j])

        for i in range(m - 1, -1, -1):
            for j in range(n):
                if i < m - 1:
                    for k in range(n):
                        dp[i][j] |= dp[i + 1][k]

        res = 0
        for i in range(n):
            res = max(res, sum(max(dp[j][i], default=0) for j in range(m)))

        return res
```

