            dp[0][j] = j
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if word1[i - 1] == word2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1]
                else:
                    dp[i][j] = 1 + min(dp[i - 1][j], dp[i][j - 1])
        if dp[n][m] > m:
            return []
        res = []
        i, j = n, m
        while j > 0:
            if word1[i - 1] == word2[j - 1]:
                res.append(i - 1)
                i -= 1
                j -= 1
            elif dp[i - 1][j] < dp[i][j - 1]:
                i -= 1
            else:
