import sys
from collections import defaultdict, deque

def bellman_ford(graph, start):
    n = len(graph)
    dist = [float('inf')] * n
    dist[start - 1] = 0
    for _ in range(n - 1):
        for u in range(1, n + 1):
            for v, w in graph[u]:
                if dist[u - 1] + w < dist[v - 1]:
                    dist[v - 1] = dist[u - 1] + w
    return dist

def has_negative_cycle(graph):
    n = len(graph)
    dist = [0] * n
    for _ in range(n - 1):
        for u in range(1, n + 1):
            for v, w in graph[u]:
                if dist[u - 1] + w < dist[v - 1]:
                    dist[v - 1] = dist[u - 1] + w
    for u in range(1, n + 1):
        for v, w in graph[u]:
            if dist[u - 1] + w < dist[v - 1]:
                return True
    return False

def dfs(graph, visited, vertex):
    visited[vertex - 1] = True
    for v, _ in graph[vertex]:
        if not visited[v - 1]:
            dfs(graph, visited, v)

def main():
    n, m = map(int, sys.stdin.readline().split())
    graph = defaultdict(list)
    for _ in range(m):
        u, v, w = map(int, sys.stdin.readline().split())
        graph[u].append((v, w))
    if has_negative_cycle(graph):
        print('No')
        return
    visited = [False] * n
    for i in range(1, n + 1):
        if not visited[i - 1]:
            dfs(graph, visited, i)
    if any(not v for v in visited):
        print('No')
        return
    min_weight = float('inf')
    for i in range(1, n + 1):
        dist = bellman_ford(graph, i)
        min_weight = min(min_weight, sum(dist))
    print(min_weight)

if __name__ == '__main__':
    main()
```

