for _ in range(N - 1):
    for u in range(1, N + 1):
        for v, w in edges[u]:
            dist[v] = min(dist[v], dist[u] + w)

q = deque()
for u in range(1, N + 1):
    for v, w in edges[u]:
        if dist[v] > dist[u] + w:
            q.append(u)
            break

while q:
    u = q.popleft()
    for v, w in edges[u]:
        if dist[v] > dist[u] + w:
            dist[v] = dist[u] + w
            q.append(v)

if all(d != float('inf') for d in dist[1:]):
    print(min(dist[1:]))
else:
