def bfs(start, end):
    queue = deque([(start, 0)])
    visited = {start}
    while queue:
        (x, y), step = queue.popleft()
        if (x, y) == end:
            return step
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < N and 0 <= ny < N and grid[nx][ny] != '#' and (nx, ny) not in visited:
                queue.append(((nx, ny), step + 1))
                visited.add((nx, ny))
    return -1

