def bfs(player):
    queue = deque([(player, 0)])
    visited = {player}
    while queue:
        (x, y), step = queue.popleft()
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < N and 0 <= ny < N and grid[nx][ny] != '#' and (nx, ny) not in visited:
