def count_divisible_by_one(x):
    return x // N + x // M - x // (N * M // math.gcd(N, M))

def binary_search(low, high):
    while low < high:
        mid = (low + high) // 2
        if count_divisible_by_one(mid) < K:
            low = mid + 1
        else:
            high = mid
    return low

print(binary_search(1, 10**18))
