import heapq
from collections import defaultdict

N, M = map(int, input().split())
trains = defaultdict(list)
for _ in range(M):
    l, d, k, c, A, B = map(int, input().split())
    for i in range(k):
        t = l + i * d
        trains[A].append((t, c, B))

dp = [-float('inf')] * (N + 1)
dp[1] = 0
pq = [(0, 1)]

while pq:
    t, node = heapq.heappop(pq)
    if t < dp[node]:
        continue
    for nt, c, nn in trains[node]:
        nt += t
        if nt > dp[nn]:
            dp[nn] = nt
            heapq.heappush(pq, (nt, nn))

for i in range(1, N):
    if dp[i] == -float('inf'):
        print("Unreachable")
    else:
        print(dp[i])
```

