from collections import Counter

def can_place_tiles(N, H, W, tiles):
    total_cells = H * W
    tile_areas = [a * b for a, b in tiles]
    area_counts = Counter(tile_areas)

    def dfs(index, remaining_cells):
        if remaining_cells == 0:
            return True
        if index == len(tile_areas):
            return False
        for _ in range(area_counts[tile_areas[index]] + 1):
            if tile_areas[index] <= remaining_cells and dfs(index + 1, remaining_cells - _ * tile_areas[index]):
                return True
        return False

    return dfs(0, total_cells)

