for _ in range(M):
    T, A, X = map(int, sys.stdin.readline().split())
    if T == 1:
        for j in range(W):
            grid[A - 1][j] = X
    else:
        for i in range(H):
            grid[i][A - 1] = X

color_count = {}
for row in grid:
    for color in row:
        if color not in color_count:
            color_count[color] = 0
        color_count[color] += 1

K = len(color_count)
print(K)
for color, count in sorted(color_count.items()):
    print(color, count)
