from collections import deque

H, W = map(int, input().split())
grid = [list(input()) for _ in range(H)]
N = int(input())
medicines = [list(map(int, input().split())) for _ in range(N)]

for i in range(H):
    for j in range(W):
        if grid[i][j] == 'S':
            start = (i, j)
        elif grid[i][j] == 'T':
            goal = (i, j)

medicines_dict = {(R-1, C-1): E for R, C, E in medicines}

queue = deque([(start, 0)])
visited = {(start, 0)}

while queue:
    (x, y), energy = queue.popleft()
    if (x, y) == goal:
        print('Yes')
        exit()
    for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] != '#':
            n_energy = energy - 1
            if (nx, ny) in medicines_dict:
                n_energy = max(n_energy, medicines_dict[(nx, ny)] - 1)
            if n_energy >= 0 and ((nx, ny), n_energy) not in visited:
                queue.append(((nx, ny), n_energy))
                visited.add(((nx, ny), n_energy))

print('No')
```

